DROP DATABASE IF EXISTS dega_hibernate_cars;

CREATE DATABASE dega_hibernate_cars;

\connect dega_hibernate_cars;

CREATE TABLE IF NOT EXISTS USERS
(
  ID       SERIAL PRIMARY KEY,
  LOGIN    TEXT UNIQUE,
  PASSWORD TEXT
);

CREATE TABLE IF NOT EXISTS CAR_BRANDS
(
  ID   SERIAL PRIMARY KEY,
  NAME TEXT UNIQUE
);

CREATE TABLE IF NOT EXISTS CAR_MODELS
(
  ID   SERIAL PRIMARY KEY,
  NAME TEXT UNIQUE
);

CREATE TABLE IF NOT EXISTS CAR_BODIES
(
  ID   SERIAL PRIMARY KEY,
  NAME TEXT UNIQUE
);

CREATE TABLE IF NOT EXISTS CAR_TRANSMISSIONS
(
  ID   SERIAL PRIMARY KEY,
  NAME TEXT UNIQUE
);

CREATE TABLE IF NOT EXISTS CAR_ENGINES
(
  ID   SERIAL PRIMARY KEY,
  NAME TEXT UNIQUE
);

CREATE TABLE IF NOT EXISTS CAR_DRIVE_TYPES
(
  ID   SERIAL PRIMARY KEY,
  NAME TEXT UNIQUE
);

CREATE TABLE IF NOT EXISTS ADVERTISEMENTS
(
  ID                  SERIAL PRIMARY KEY,
  DESCRIPTION         TEXT,
  SOLD                BOOLEAN,
  PHOTO               BYTEA,
  USER_ID             INTEGER REFERENCES USERS (ID) ON UPDATE CASCADE ON DELETE CASCADE,
  CAR_BRAND_ID        INTEGER REFERENCES CAR_BRANDS (ID) ON UPDATE CASCADE ON DELETE CASCADE,
  CAR_MODEL_ID        INTEGER REFERENCES CAR_MODELS (ID) ON UPDATE CASCADE ON DELETE CASCADE,
  CAR_BODY_ID         INTEGER REFERENCES CAR_BODIES (ID) ON UPDATE CASCADE ON DELETE CASCADE,
  CAR_TRANSMISSION_ID INTEGER REFERENCES CAR_TRANSMISSIONS (ID) ON UPDATE CASCADE ON DELETE CASCADE,
  CAR_ENGINE_ID       INTEGER REFERENCES CAR_ENGINES (ID) ON UPDATE CASCADE ON DELETE CASCADE,
  CAR_DRIVE_TYPE_ID   INTEGER REFERENCES CAR_DRIVE_TYPES (ID) ON UPDATE CASCADE ON DELETE CASCADE
);

